import React from 'react';
import './App.css';
import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'
import Sidebar from './Sidebar';
import CourseArea from './CourseArea';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      allCourses: {},
      filteredCourses: {},
      cartCourses: [],
      subjects: []
    };
  }

  componentDidMount() {
    fetch('http://mysqlcs639.cs.wisc.edu:53706/api/react/classes').then(
      res => res.json()
    ).then(data => this.setState({ allCourses: data, filteredCourses: data, cartCourses: [], subjects: this.getSubjects(data) }));
  }

  getSubjects(data) {
    let subjects = [];
    subjects.push("All");
    for (const course of Object.values(data)) {
      if (subjects.indexOf(course.subject) === -1)
        subjects.push(course.subject);
    }
    return subjects;
  }

  setCourses(courses) {
    this.setState({ filteredCourses: courses })
  }

  updateCourse(status, course, section, subsection) {
    // add a course
    if (status === "add" && section == null && subsection == null) {
      this.addCourse(course);
    }
    // remove a course
    else if (status === "remove" && section == null && subsection == null) {
      this.removeCourse(course);
    }
    // add a section
    else if (status === "add" && subsection == null) {
      this.addSection(course, section);
    }
    // remove a section
    else if (status === "remove" && subsection == null) {
      this.removeSection(course, section);
    }
  }

  addCourse(course) {
    // make sure that there are no duplicated courses in the cart
    for (let i = 0; i < this.state.cartCourses.length; i++) {
      if (this.state.cartCourses[i].number === course.number) {
        this.removeCourse(course);
        break;
      }
    }
    this.state.cartCourses.push(course);
    // console.log(this.state.cartCourses);
  }

  removeCourse(course) {
    for (let i = 0; i < this.state.cartCourses.length; i++) {
      if (this.state.cartCourses[i].number === course.number) {
        this.state.cartCourses.splice(i, i + 1);
        console.log(this.state.cartCourses);
        return;
      }
    }
    // console.log(this.state.cartCourses);
  }

  addSection(course, section) {
    // course already exists
    for (let i = 0; i < this.state.cartCourses.length; i++) {
      if (this.state.cartCourses[i].number === course.number) {
        // remove the section if the section already exists
        console.log("course already exists");
        for (let j = 0; j < this.state.cartCourses[i].sections.length; j++) {
          if (this.state.cartCourses[i].sections[j].number == section.number) {
            console.log("section already exists");
            this.removeSection(course, section);
            break;
          }
        }
        // add the section
        this.state.cartCourses[i].sections.push(section);
        console.log(this.state.cartCourses);
        return;
      }
    }
    console.log("course not exists");
    // course not exists
    course.sections = [];
    course.sections.push(section);
    this.addCourse(course);
    // for (let i = 0; i < this.state.cartCourses.length; i++) {
    //   if (this.state.cartCourses[i].number === course.number) {
    //     // remove the section that is not specified section 
    //     for (let j = 0; j < this.state.cartCourses[i].sections.length; j++) {
    //       if (this.state.cartCourses[i].sections[j].number != section.number) {
    //         console.log("remove: " + section);
    //         this.removeSection(course, section);
    //       }
    //     }
    //     break;
    //   }
    // }
    console.log(this.state.cartCourses);
  }

  removeSection(course, section) {
    for (let i = 0; i < this.state.cartCourses.length; i++) {
      if (this.state.cartCourses[i].number === course.number) {
        // remove the section if the section already exists
        for (let j = 0; j < this.state.cartCourses[i].sections.length; j++) {
          if (this.state.cartCourses[i].sections[j].number === section.number) {
            console.log(this.state.cartCourses[i].sections);
            this.state.cartCourses[i].sections.splice(j, j + 1);
            console.log(this.state.cartCourses[i].sections);
            return;
          }
        }
      }
    }
    console.log(this.state.cartCourses);
  }

  render() {
    return (
      <>
        <link
          rel="stylesheet"
          href="https://maxcdn.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
          integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T"
          crossOrigin="anonymous"
        />
        <Tabs defaultActiveKey="search" style={{ position: 'fixed', zIndex: 1, width: '100%', backgroundColor: 'white' }}>
          <Tab eventKey="search" title="Search" style={{ paddingTop: '5vh' }}>
            <Sidebar setCourses={(courses) => this.setCourses(courses)} courses={this.state.allCourses} subjects={this.state.subjects} />
            <div style={{ marginLeft: '20vw' }}>
              <CourseArea data={this.state.filteredCourses} allData={this.state.allCourses} cartCourses={this.state.cartCourses} updateCourse={this.updateCourse.bind(this)} cartMode={false} />
            </div>
          </Tab>

          <Tab eventKey="cart" title="Cart" style={{ paddingTop: '5vh' }}>
            <div style={{ marginLeft: '5vw' }}>
              <CourseArea data={this.state.cartCourses} allData={this.state.allCourses} cartCourses={this.state.cartCourses} updateCourse={this.updateCourse.bind(this)} cartMode={true} />
            </div>
          </Tab>
        </Tabs>
      </>
    )
  }
}

export default App;
