import React from 'react';
import './App.css';
import Section from './Section'

class Course extends React.Component {

  setAllKeywords() {
    var keywordsString = "";
    var keyword_list = this.props.data.keywords;
    for (let i = 0; i < keyword_list.length; i++) {
      keywordsString = keywordsString.concat(keyword_list[i]);
      if (i != keyword_list.length - 1) {
        keywordsString = keywordsString.concat(", ");
      }
    }
    return keyword_list.join(", ");
  }

  setAllRequisites() {
    var allRequisites = this.props.data.requisites;
    if (allRequisites.length == 0) {
      return "None";
    }
    var requisitesString = "";
    for (let i = 0; i < allRequisites.length; i++) {
      for (let j = 0; j < allRequisites[i].length; j++) {
        if (j == 0) {
          requisitesString = requisitesString.concat("(");
        }
        requisitesString = requisitesString.concat(allRequisites[i][j]);
        if (j != allRequisites[i].length - 1) {
          requisitesString = requisitesString.concat(" OR ");
        } else {
          requisitesString = requisitesString.concat(")");
        }
      }
      if (i != allRequisites.length - 1) {
        requisitesString = requisitesString.concat(" AND ");
      }
    }
    return requisitesString;
  }

  render() {
    return (
      <div>
        <h1> <b>({this.props.data.number}) {this.props.data.name}</b> | ({this.props.data.credits} Credits)</h1>
        <h3> <b>Subject</b>: {this.props.data.subject} </h3>
        <h4> {this.props.data.description} </h4>
        <h3> <b>Requisites</b>: {this.setAllRequisites()} </h3>
        <h4> <b>Keywords</b>: {this.setAllKeywords()} </h4>
        <h2> Sections </h2>
      </div>
    )
  }
}

export default Course;
