import React from 'react'
import { Button, Accordion, Card } from 'react-bootstrap'
import './App.css'
import Subsection from './Subsection.js'


function displayOneSection(section) {
	// console.log(section);
	return (
		<div>
			<h4> <b>{section.number}</b></h4>
			<h5> <b>Instructor</b>: {section.instructor}</h5>
			<h5> <b>Location</b>: {section.location}</h5> 
			<h5> <b>Location</b>: {section.time}</h5>
			{/* <div>
				{(section.time).map(time => (
					// console.log(time)
					<li> {time} </li>
				))}
			</div> */}
		</div>
	)

}


class Section extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			data: props.data,
		}
	}
	displayAllSections() {
		console.log(this.state.data);
		var allSections = this.state.data.sections;
		return (
			<div>
				{allSections.map(section => (
					displayOneSection(section)
				))}
			</div>
		)
	}
	render() {
		return (
			<div>
				{this.displayAllSections()}
			</div>
		)
	}
}

export default Section;
