import React from 'react';
import './App.css';
import Section from './Section'

class Course extends React.Component {

  constructor(props) {
		super(props);
		this.state = {
      key: props.key,
      course: props.course,
		}
	}

  setAllKeywords() {
    return this.state.course.keywords.join(", ");
  }

  setAllRequisites() {
    var allRequisites = this.state.course.requisites;
    if (allRequisites.length == 0) {
      return "None";
    }
    var requisitesString = "";
    for (let i = 0; i < allRequisites.length; i++) {
      for (let j = 0; j < allRequisites[i].length; j++) {
        if (j == 0) {
          requisitesString = requisitesString.concat("(");
        }
        requisitesString = requisitesString.concat(allRequisites[i][j]);
        if (j != allRequisites[i].length - 1) {
          requisitesString = requisitesString.concat(" OR ");
        } else {
          requisitesString = requisitesString.concat(")");
        }
      }
      if (i != allRequisites.length - 1) {
        requisitesString = requisitesString.concat(" AND ");
      }
    }
    return requisitesString;
  }

  getCartCourse() {
    for (let i = 0; i < this.props.cartCourses.length; i++) {
      if (this.props.cartCourses[i].number === this.props.course.number) {
        return this.props.cartCourses[i];
      }
    }
    return null;
  }

  addCourse() {
    // check that there are no duplicated courses in the cart
    for (let i = 0; i < this.props.cartCourses.length; i++) {
      if (this.props.cartCourses[i].number === this.props.course.number) {
        return;
      }
    }
    this.props.cartCourses.push(this.state.course);
    console.log(this.props.cartCourses);
  }

  render() {
    if (!this.props.cartMode) {
      return (
        <div>
          <h1> 
            <b>({this.state.course.number}) {this.state.course.name}</b> | ({this.state.course.credits} Credits) <button onClick={()=>this.addCourse()}> Add Course</button>
          </h1>
          <h3> <b>Subject</b>: {this.state.course.subject} </h3>
          <h4> {this.state.course.description} </h4>
          <h3> <b>Requisites</b>: {this.setAllRequisites()} </h3>
          <h4> <b>Keywords</b>: {this.setAllKeywords()} </h4>
          <h2> <b>Sections</b></h2>
          <Section data={this.state.course} cartMode={this.props.cartMode} cartCourse={this.getCartCourse()}></Section>
        </div>
      )
    } else {
      return (
        <div>
          <h1> <b>({this.state.course.number}) {this.state.course.name}</b> | ({this.state.course.credits} Credits) <button> Remove Course</button></h1>
          <h3> <b>Subject</b>: {this.state.course.subject} </h3>
          <h4> {this.state.course.description} </h4>
          <h3> <b>Requisites</b>: {this.setAllRequisites()} </h3>
          <h4> <b>Keywords</b>: {this.setAllKeywords()} </h4>
          <h2> <b>Sections</b> </h2>
          <Section data={this.state.course} cartMode={this.props.cartMode}></Section>
        </div>
      )
    }
  }
}

export default Course;
